import './index.scss';
import { useState, useEffect } from 'react';
import AnimatedLetters from '../AnimatedLetters';
import React from 'react';
import ImageCarousel from './ImageCarousel';


const Modal = ({ isOpen, onClose, project }) => {
    const [letterClass, setLetterClass] = useState('text-animate');

    useEffect(() => {
        if (isOpen) {
            setLetterClass('text-animate'); // Reset animation when modal opens
            const timer = setTimeout(() => {
                setLetterClass('text-animate-hover')
            }, 6000);
            return () => clearTimeout(timer);
        }
    }, [isOpen]);

    if (!isOpen) return null;

    return (
        <div className="modal-overlay">
            <div className="modal-content">
                <button className="modal-close" onClick={onClose}>Ã—</button>
                    <div className="modal-header">
                        <h1>
                            <AnimatedLetters
                                letterClass={letterClass}
                                strArray={project.title.split('')}
                                idx={5}
                            />
                        </h1>
                    </div>
                <div className="modal-body">
                    <ImageCarousel images={project.images}/>
                    <div className="project-details">
                        <h3>{project.description1}</h3>
                        <h1>{project.description2}</h1>
                        {/* Add more project details here */}
                        <div className="project-description">
                            {project.technologies}
                        </div>
                        {project.paragraphs && project.paragraphs.map((paragraph, idx) => (
                            <p key={idx}>
                                {paragraph.split('Illinois').map((part, i, arr) => {
                                    if (i < arr.length - 1) {
                                        return (
                                            <>
                                                {part}
                                                <a 
                                                    href="https://illinois.edu" 
                                                    target="_blank" 
                                                    rel="noopener noreferrer"
                                                    className="paragraph-link"
                                                >
                                                    Illinois
                                                </a>
                                            </>
                                        );
                                    }
                                    return part;
                                })}
                            </p>
                        ))}
                    </div>
                </div>
            </div>
        </div>
    )
}

export default Modal;
