import AnimatedLetters from '../AnimatedLetters';
import './index.scss';
import Loader from 'react-loaders';
import { useState, useEffect, useRef } from 'react';
import emailjs from '@emailjs/browser'

const Contact = () => {

    const [letterClass, setLetterClass] = useState('text-animate');

    const refForm = useRef();

    useEffect(() => {
            const timer = setTimeout(() => {
                setLetterClass('text-animate-hover')
            }, 3000);
    
            return () => clearTimeout(timer);
        }, []);


    const sendEmail = (e) => {
        e.preventDefault();

        emailjs.sendForm(
            'service_5bxowb2', // YOUR SERVICE ID
            'template_n5ysknc', // YOUR TEMPLATE ID
            refForm.current,
            'w3VJtJDOkuuufi5B8' // YOUR PUBLIC KEY
        )
        .then (
            () => {
                alert('Message successfully sent! Please wait for my response in the next 48 hours.')
                window.location.reload(false);
            },
            (error) => {
                console.log('Failed: ', error.text)
                alert('Failed to send the message, please try again or send manual email.');
            }
        )
    }
    return (
    <>
        <div className = 'container contact-page'>
            <div className = 'text-zone'>
            <h1>
                <AnimatedLetters
                strArray={['C', 'o', 'n', 't', 'a', 'c', 't', ' ', 'M', 'e']}
                letterClass={letterClass}
                idx = {15}
                />
            </h1>
            <p>
            I am interested in infrastructure and development opportunities, especially for large and ambitious
            projects. If you are interested in my work or would like to talk about a 
            unique opportunity, feel free to contact me using the form below or emailing me at aneeshg5@illinois.edu,
            and I will respond within 48 hours.
            </p>
            <div className="contact-form">
                <form ref = {refForm} onSubmit={sendEmail}>
                    <ul>
                        <li className = "half">
                            <input type="text" name="name" placeholder='Name' required autocomplete="new-password"/>
                        </li>
                        <li className = "half">
                            <input type="email" name="email" placeholder='Email' required autocomplete="new-password"/>
                        </li>
                        <li>
                            <input placeholder="Subject" type="text" name="subject" required autocomplete="new-password"/>
                        </li>
                        <li>
                            <textarea placeholder="Message" name="message" required></textarea>
                        </li>
                        <li>
                            <input type="submit" className="flat-button" value="SEND" />
                        </li>
                    </ul>
                </form>
            </div>
            </div>
            <div className='info-map'>
                Aneesh Ganti,
                <br/>
                United States of America,
                <br />
                Urbana, IL & San Francisco, CA
                <br />
                <span>Email address: aneeshg5@gmail.com</span>
            </div>
        </div>
        <Loader type="pacman" />
    </>)
};

export default Contact;
