import AnimatedLetters from '../AnimatedLetters';
import './index.scss';
import { useState, useEffect } from 'react';
// import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
// import {
//     faAngular,
//     faCss3,
//     faGitAlt,
//     faHtml5,
//     faJsSquare,
//     faReact,
//   } from '@fortawesome/free-brands-svg-icons'

import image1 from '../../assets/images/Headshot - Copy.jpeg';
import image2 from '../../assets/images/lego_shelf.png';
import image3 from '../../assets/images/taj_mahal.png';
import image4 from '../../assets/images/talk.png';
import image5 from '../../assets/images/pismo_beach.png';
import image6 from '../../assets/images/avionics (2).png';


const About = () => {
    
    const [letterClass, setLetterClass] = useState('text-animate');

    useEffect(() => {
            const timer = setTimeout(() => {
                setLetterClass('text-animate-hover')
            }, 3000);
    
            return () => clearTimeout(timer);
        }, []);

    return (
        <div className='container about-page'>
            <div className='text-zone'>
                <h1>
                    <AnimatedLetters 
                        letterClass={letterClass}
                        strArray={['A', 'b', 'o', 'u', 't', ' ', 'M', 'e']}
                        idx = {15}
                    />
                </h1>
                <p>
                I am a Computer Science and Mathematics student at the University of Illinois Urbana-Champaign.
                I am from the Bay Area in California, and prior to that, I lived in Austin, Texas for 6 years.
                </p>
                <p>
                I am proficient with C++, Python, JavaScript, building machine learning models, and data analysis.
                I thrive on solving complex problems, creating efficient and scalable solutions, and
                staying up-to-date with industry practices.
                </p>
                <p>
                Currently, I am involved in a lot of software development, engineering teams, rocketry, and
                undergraduate research. Some cool hobbies include traveling, building and designing LEGO sets, and
                learning about cool new things.
                </p>
            </div>
            <div className='stage-cube-cont'>
                <div className='cubespinner'>
                    <div className = 'face1'>
                        <img src={image5} alt="Technology 1" />
                    </div>
                    <div className = 'face2'>
                        <img src={image6} alt="Avionics" />
                    </div>
                    <div className = 'face3'>
                        <img src={image3} alt="Taj Mahal" />
                    </div>
                    <div className = 'face4'>
                        <img src={image3} alt="Lego-shelf" />
                    </div>
                    <div className = 'face5'>
                        <img src={image2} alt="UCSB Talk" />
                    </div>
                    <div className = 'face6'>
                        <img src={image} alt="Pismo Beach" />
                    </div>
                </div>
            </div>
        </div>
    )
}

export default About;
